mapper.select:
SELECT 
	
FROM account_inquiry a

WHERE a.id = #{id}

mapper.update:
UPDATE account_inquiry SET 
	del_flag = 1
WHERE id = #{id}

UPDATE account_inquiry SET 
	status = 1
WHERE ordernum = #{ordernum}

mapper.select:
SELECT 
	
FROM account_inquiry a

	where 1=1 
	
	    	AND a.create_date > #{beginDate}
	     
	    
	    	AND a.create_date < #{endDate}
	    
	    
	    	AND a.status = #{status}
	    
	    
	
		
	
	
		ORDER BY a.create_date DESC

mapper.update:
UPDATE account_inquiry SET 	
	status=#{status}
