mapper.select:
SELECT 

FROM account_inspection_detail a

WHERE a.id = #{id}

SELECT 

FROM account_inspection_detail a

where
	a.del_flag = '0'

		AND a.parent_id = #{parent_Id}


		AND a.materialname = #{materialname}


		AND a.inspectionmode = #{inspectionmode}

mapper.update:
UPDATE account_inspection_detail SET 
	del_flag = '1' 
	WHERE id = #{id}
