mapper.select:
SELECT 

FROM account_purchase_detail a

WHERE a.id = #{id}

SELECT a.*,c.`name` AS packways,d.`name` AS transports,e.materialName AS materialname,f.supplier FROM account_purchase_detail a LEFT JOIN account_para_info c ON a.packWay=c.id LEFT JOIN account_para_info d ON a.transport=d.id LEFT JOIN account_material e ON a.materialCode=e.materialNum LEFT JOIN account_supplier f ON a.supplierCode=f.supplierNum
a.del_flag =0
AND a.parent_id = #{parent.id}

mapper.update:
UPDATE account_purchase_detail SET 
	del_flag = 1


		WHERE id = #{id}


		WHERE parent_id = #{parent.id}
