mapper.select:
    SELECT 
	  
	FROM
	  account_material a 
	WHERE 1 = 1 
	  AND a.`del_flag` = 0 
	
      AND a.materialName like CONCAT(CONCAT('%',#{mName}),'%') 
    
	
      AND DATE_FORMAT(a.create_date, '%Y-%m-%d') >= #{sTime} 
    
	
      AND DATE_FORMAT(a.create_date, '%Y-%m-%d') <= #{eTime} 
    
    LIMIT
		#{begin},#{end}

SELECT count(*) AS count FROM account_material a WHERE 1 = 1 AND a.`del_flag` = 0
AND a.materialName like CONCAT(CONCAT('%',#{mName}),'%')
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') >= #{sTime}
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') <= #{eTime}
